mapper.select:
select pid
from purchase
where uid = #{sessionId} and lid = #{lid}

select payid
from purchase
where pid = #{pid}

select count(*)
from purchase
where lid = #{lid}

select count(*)
from purchase p inner join lecture l
  on p.lid = l.lid
where p.uid = #{uid}
  and l.startdate = #{startdate}
  and l.timesession = #{timesession}
