mapper.select:
SELECT uid, email, pwd, uname, role, disable
FROM user
WHERE uid = #{uid}

SELECT lid , subject , lname , price , startdate , SUBSTRING_INDEX(timesession,'*', -1) AS timesession FROM lecture WHERE teacher_id = #{uid}
AND confirm = 1
AND startdate < #{curr_day}
or ( startdate = #{curr_day} and SUBSTRING(timesession, 5, 1) < #{curr_session} )
ORDER BY startdate DESC, timesession DESC
AND confirm = 1
AND startdate > #{curr_day}
or ( startdate = #{curr_day} and SUBSTRING(timesession, 5, 1) >= #{curr_session} )
ORDER BY startdate, timesession
AND confirm = 0 ORDER BY startdate, timesession

SELECT count(*) FROM lecture WHERE teacher_id = #{uid}
AND confirm = 1
AND startdate < #{curr_day}
or ( startdate = #{curr_day} and SUBSTRING(timesession, 5, 1) < #{curr_session} )
ORDER BY startdate DESC, timesession DESC
AND confirm = 1
AND startdate > #{curr_day}
or ( startdate = #{curr_day} and SUBSTRING(timesession, 5, 1) >= #{curr_session} )
ORDER BY startdate, timesession
AND confirm = 0 ORDER BY startdate, timesession

select count(*)
from lecture
where teacher_id = #{uid}
      and startdate = #{startdate}
      and timesession = #{timesession}
