mapper.select:
select uid
from user
where email=#{email}

select role
from user
where uid = #{sessionId}

mapper.insert:
insert into user (email
                , pwd
                , uname
                , role
                )
values (#{email}
      , #{pwd}
      , #{uname}
      , #{role}
      )

mapper.select:
select count(email)
from user
where email=#{email}

mapper.update:
update user
set email = #{email}
  , uname = #{uname}

  , pwd = #{pwd}

where uid = #{uid}

update user
set disable = 1
where uid = #{uid}
